process.env.IS_TEST = true;
process.env.LOG_LEVEL = 1;

const crypto = require('crypto');
const mod = require('../src/handler.js');
const itemStore = require('../src/lib/itemStore.js');
const talkStore = require('../src/lib/talkStore.js');
